core/account/_private/layout:
{{!
  A wrapper for all account pages.

  @block account-heading
         The page's title.

  @block account-content
         The content for the main part of the layout.

  @param class string
         A name for the current page, added as a class to the account wrapper.

  @param nav string
         If "hidden", no nav will be shown.

  @param nav_page string
         If matches a string in navigation.html, that link will be made active.

  @param header string
         If "hidden", no header will be shown.
         If "top", header will be shown above the nav.
         Otherwise, header will be shown above the content.
}}
<section class="account-wrapper account-page-{{class}}{{#if nav '===' 'hidden'}} account-nav-hidden{{/if}}">
  {{#if header '===' 'top'}}{{> core/account/_private/layout-heading}}{{/if}}

  {{#if nav '!==' 'hidden'}}
    {{> core/account/_private/navigation nav_page=nav_page}}
  {{/if}}

  <div class="account-body">
    {{#unless header}}{{> core/account/_private/layout-heading}}{{/unless}}

    <div class="account-content">
      {{#block 'account-content'}}{{/block}}
    </div>
  </div>
</section>

core/account/add-wishlist:
{{#partial 'account-heading'}}
  {{#if forms.wishlist.id}}
    {{lang 'core.account.wishlists.edit'}}
  {{else}}
    {{lang 'core.account.wishlists.new'}}
  {{/if}}
{{/partial}}

{{#partial 'account-content'}}
  <div class="wishlist-add">
    <header class="wishlist-header">
      {{#if forms.wishlist.errors}}
        {{> core/alerts/alert-error forms.wishlist.errors}}
      {{/if}}
    </header>

    <form class="form wishlist-form" action="{{#if forms.wishlist.id}}{{urls.account.wishlists.edit}}{{else}}{{urls.account.wishlists.add}}{{/if}}" method="post" data-validated-form>
      <input type="hidden" name="submit">

      {{> core/forms/common/text required=true name="wishlistname" id="wishlistname" value=forms.wishlist.name label=(lang 'core.account.wishlists.wishlist_name')}}

      <div class="form-field form-field-checkbox">
        <div class="form-field-control">
          <label class="form-label">
            <input
              class="form-checkbox"
              name="publicwishlist"
              type="checkbox"
              {{#if forms.wishlist.is_public}}checked{{/if}}
              aria-required="false">
            <span class="form-label-text">{{lang 'core.account.wishlists.make_public'}}</span>
          </label>
        </div>
      </div>

      <div class="form-actions">
        {{#if forms.wishlist.id}}
          <input type="submit" class="button account-button-primary" value="{{lang 'core.account.wishlists.save'}}">
          <input type="hidden" name="wishlistid" value="{{forms.wishlist.id}}">
        {{else}}
          <input type="submit" class="button account-button-primary" value="{{lang 'core.account.wishlists.create'}}">
        {{/if}}
      </div>

    </form>
  </div>
{{/partial}}

{{> core/account/_private/layout nav_page='wishlists' class='add-wishlist'}}
