core/account/_private/layout:
{{!
  A wrapper for all account pages.

  @block account-heading
         The page's title.

  @block account-content
         The content for the main part of the layout.

  @param class string
         A name for the current page, added as a class to the account wrapper.

  @param nav string
         If "hidden", no nav will be shown.

  @param nav_page string
         If matches a string in navigation.html, that link will be made active.

  @param header string
         If "hidden", no header will be shown.
         If "top", header will be shown above the nav.
         Otherwise, header will be shown above the content.
}}
<section class="account-wrapper account-page-{{class}}{{#if nav '===' 'hidden'}} account-nav-hidden{{/if}}">
  {{#if header '===' 'top'}}{{> core/account/_private/layout-heading}}{{/if}}

  {{#if nav '!==' 'hidden'}}
    {{> core/account/_private/navigation nav_page=nav_page}}
  {{/if}}

  <div class="account-body">
    {{#unless header}}{{> core/account/_private/layout-heading}}{{/unless}}

    <div class="account-content">
      {{#block 'account-content'}}{{/block}}
    </div>
  </div>
</section>

core/account/inbox:
{{#partial 'account-heading'}}
  {{lang 'core.account.messages.heading'}}
{{/partial}}

{{#partial 'account-toolbar'}}
  {{#if customer.inbox.orders}}
    <button class="account-tool" data-account-toggle=".account-messages-form">{{lang 'core.account.inbox.show_form'}}</button>
  {{/if}}
{{/partial}}

{{#partial 'account-content'}}
  {{#if forms.inbox.error}}
    {{> core/alerts/alert-error message=forms.inbox.error dismissable=true}}
  {{/if}}

  {{#if forms.inbox.success}}
    {{> core/alerts/alert-success message=forms.inbox.success dismissable=true}}
  {{/if}}

  {{#if customer.inbox.orders}}
    {{> core/account/_private/messages-form}}
  {{/if}}

  {{> core/account/_private/messages-list messages=customer.inbox.messages}}
{{/partial}}

{{> core/account/_private/layout nav_page='messages' class='messages'}}

core/forms/common/multiline:
{{!
  Textarea form component

  @param validation_message string
         Custom validation message
}}
<div class="form-field form-field-common form-field-multiline {{#if required}}form-required{{/if}}" id="{{id}}">
  <label class="form-label">
    <span class="form-field-title">
      {{label}}
      {{#if required}}<span class="required-text">{{lang 'common.required'}}</span>{{/if}}
    </span>
    <span class="form-field-control">
      <textarea
        class="form-input"
        id="textarea-{{id}}"
        name="{{name}}"
        placeholder="{{prefill}}"
        rows="{{rows}}"
        {{#if required}}
          required
          {{#if validation_message}}
            data-vd-message-required="{{validation_message}}"
          {{/if}}
        {{/if}}
        aria-required="{{required}}">{{value}}</textarea>
    </span>
  </label>
</div>
