    <div class="banners banners-top">
      {{#each this.top}}
        <div class="banner rte">
          {{{this}}}
        </div>
      {{/each}}
    </div>
  {{/if}}
{{/if}}

{{#if location '===' 'bottom'}}
  {{#if this.bottom}}
    <div class="banners banners-bottom">
      {{#each this.bottom}}
        <div class="banner rte">
          {{{this}}}
        </div>
      {{/each}}
    </div>
  {{/if}}
{{/if}}

components/common/facebook-like:
{{#any settings.add_this.buttons service='facebook_like'}}
  <div id="fb-root"></div>
  <script>(function(d, s, id) {
    var js, fjs = d.getElementsByTagName(s)[0];
    if (d.getElementById(id)) return;
    js = d.createElement(s); js.id = id;
    js.src = "//connect.facebook.net/en_US/sdk.js#xfbml=1&version=v2.5";
    fjs.parentNode.insertBefore(js, fjs);
  }(document, 'script', 'facebook-jssdk'));</script>
{{/any}}

components/currency-selector:
{{#if currency_selector.currencies.length '>' 1}}
  <div class="currency-selector-wrapper">
    <label class="currency-selector-label" for="currency-switcher">
      <span data-currency-selector-toggle>{{currency_selector.active_currency_code}}
        {{> components/common/icons/down-arrow}}
      </span>
    </label>
      <select class="currency-selector">
        {{#each currency_selector.currencies}}
          <option value="{{switch_url}}" {{#if is_active}}selected{{/if}}>
            {{name}}
          </option>
        {{/each}}
      </select>
  </div>
{{/if}}

