core/account/_private/layout:
{{!
  A wrapper for all account pages.

  @block account-heading
         The page's title.

  @block account-content
         The content for the main part of the layout.

  @param class string
         A name for the current page, added as a class to the account wrapper.

  @param nav string
         If "hidden", no nav will be shown.

  @param nav_page string
         If matches a string in navigation.html, that link will be made active.

  @param header string
         If "hidden", no header will be shown.
         If "top", header will be shown above the nav.
         Otherwise, header will be shown above the content.
}}
<section class="account-wrapper account-page-{{class}}{{#if nav '===' 'hidden'}} account-nav-hidden{{/if}}">
  {{#if header '===' 'top'}}{{> core/account/_private/layout-heading}}{{/if}}

  {{#if nav '!==' 'hidden'}}
    {{> core/account/_private/navigation nav_page=nav_page}}
  {{/if}}

  <div class="account-body">
    {{#unless header}}{{> core/account/_private/layout-heading}}{{/unless}}

    <div class="account-content">
      {{#block 'account-content'}}{{/block}}
    </div>
  </div>
</section>

core/auth/login:
{{#partial 'account-content'}}
  {{#if forms.login.error}}
    {{> core/alerts/alert-error forms.login.error}}
  {{/if}}

  {{#if forms.login.success}}
    {{> core/alerts/alert-success forms.login.success}}
  {{/if}}

  <div class="account-login-wrapper">
    <div class="account-login">
      <h2 class="account-sub-heading">{{lang 'core.auth.login.login_form.heading'}}</h2>

      <form class="account-login-form form" action="{{urls.auth.check_login}}" method="post" data-validated-form>
        {{> core/forms/common/email required=true label=(lang 'core.auth.login.login_form.email_address') id='login_email' name='login_email'}}
        {{> core/forms/common/password required=true label=(lang 'core.auth.login.login_form.password') id='login_pass' name='login_pass'}}

        <div class="form-actions">
          <input type="submit" class="button account-button-primary account-login-button" value="{{lang 'core.auth.login.login_form.submit'}}">
          <a class="account-forgot-password-link" href="{{urls.auth.forgot_password}}">{{lang 'core.auth.login.login_form.forgot_password'}}</a>
        </div>
      </form>
    </div>

    <div class="account-create">
      <h2 class="account-sub-heading">{{lang 'core.auth.login.create_form.heading'}}</h2>
      <p class="account-create-intro">{{lang 'core.auth.login.create_form.intro'}}</p>
      <ul class="account-create-facts">
        <li>{{lang 'core.auth.login.create_form.fact_faster'}}</li>
        <li>{{lang 'core.auth.login.create_form.fact_addresses'}}</li>
        <li>{{lang 'core.auth.login.create_form.fact_order_history'}}</li>
        <li>{{lang 'core.auth.login.create_form.fact_track_orders'}}</li>
        {{#if settings.show_wishlist}}
          <li>{{lang 'core.auth.login.create_form.fact_wishlist'}}</li>
        {{/if}}
      </ul>
      <a class="button account-button-primary" href="{{urls.auth.create_account}}">{{lang 'core.auth.login.create_form.create_account'}}</a>
    </div>
  </div>
{{/partial}}

{{> core/account/_private/layout class='login' nav='hidden' header='hidden'}}
