core/account/_private/layout:
{{!
  A wrapper for all account pages.

  @block account-heading
         The page's title.

  @block account-content
         The content for the main part of the layout.

  @param class string
         A name for the current page, added as a class to the account wrapper.

  @param nav string
         If "hidden", no nav will be shown.

  @param nav_page string
         If matches a string in navigation.html, that link will be made active.

  @param header string
         If "hidden", no header will be shown.
         If "top", header will be shown above the nav.
         Otherwise, header will be shown above the content.
}}
<section class="account-wrapper account-page-{{class}}{{#if nav '===' 'hidden'}} account-nav-hidden{{/if}}">
  {{#if header '===' 'top'}}{{> core/account/_private/layout-heading}}{{/if}}

  {{#if nav '!==' 'hidden'}}
    {{> core/account/_private/navigation nav_page=nav_page}}
  {{/if}}

  <div class="account-body">
    {{#unless header}}{{> core/account/_private/layout-heading}}{{/unless}}

    <div class="account-content">
      {{#block 'account-content'}}{{/block}}
    </div>
  </div>
</section>

core/account/addresses:
{{#partial 'account-heading'}}
  {{lang 'core.account.addresses.heading'}}
{{/partial}}

{{#partial 'account-toolbar'}}
  <a class="account-tool" href="{{urls.account.add_address}}">
    {{lang 'core.account.addresses.new_address'}}
  </a>
{{/partial}}

{{#partial 'account-content'}}
  {{#unless customer.addresses}}
    {{> core/alerts/alert-info (lang 'core.account.addresses.no_addresses')}}
  {{/unless}}

  <div class="account-items">
    {{#each customer.addresses}}
      <article class="account-item">
        <div class="account-item-body">
          <h1 class="account-item-title account-item-title-large">
            {{first_name}} {{last_name}}
          </h1>

          <div class="account-item-details">
            <span class="account-address-detail">{{company}}</span>
            <span class="account-address-detail">{{address1}}</span>
            <span class="account-address-detail">{{address2}}</span>
            <span class="account-address-detail">{{city}}, {{state}} {{zip}}</span>
            <span class="account-address-detail">{{lang 'core.account.addresses.phone' phone_number=phone}}</span>
          </div>
        </div>
        <div class="account-item-actions">
          <form class="form" action="{{delete_url}}" method="post">
            <a class="button account-button-primary account-button-small" href="{{edit_url}}">{{lang 'core.account.common.edit'}}</a>
            <button type="submit" class="button account-button-primary account-button-small">{{lang 'core.account.common.delete'}}</button>
          </form>
        </div>
      </article>
    {{/each}}
  </div>
{{/partial}}

{{> core/account/_private/layout nav_page='addresses' class='addresses'}}
