components/sitemap/group:
{{!
  @param items
         A sitemap tree. Each item can have a `url`, `label`, and `children`.
}}
{{#if items.length}}
  <ul>
    {{#each items}}
      <li>
        <a href="{{url}}">{{label}}</a>

        {{#if children}}
          {{> components/sitemap/group items=children}}
        {{/if}}
      </li>
    {{/each}}
  </ul>
{{/if}}
