core/account/_private/account-product-it:
{{!
  A single product, as shown in a listing.

  @param this object
         A product object.

  @param show_quickshop bool
         If true, include a link that can be used to open a quickshop modal.

  @param show_compare bool
         If true, include a checkbox to select products to compare.

  @param show_remove bool
         If true, include remove from cart form. Requires `remove_url` to be
         available.

  @param show_rating bool
         If true, include the product's rating if there are enough votes.
}}
<article class="account-product-item">
  <figure class="account-product-item-thumbnail" {{#if show_bg}}style="background-image: url({{getImage image 'account-product-thumbnail' (cdn default_image)}})"{{/if}}>
    <a href="{{url}}" title="{{name}}">
      <img src="{{getImage image 'account-product-thumbnail' (cdn default_image)}}" alt="{{image.alt}}">
    </a>

    {{#if price.saved}}
      <div class="account-product-item-sale-tag">{{lang 'core.product.sale'}}</div>
    {{/if}}

    {{#if show_remove}}
      <form class="form account-product-item-remove" action="{{remove_url}}" method="post">
        <input type="submit" value="&times;">
      </form>
    {{/if}}
  </figure>

  <div class="account-product-item-details">
    {{#if show_rating}}
      {{> core/products/rating rating=rating}}
    {{/if}}

    <h3 class="account-product-item-title">
      <a href="{{url}}" title="{{name}}">{{name}}</a>
    </h3>

    {{#if brand.name}}
      <span class="account-product-item-brand">
        <a href="{{brand.url}}">{{brand.name}}</a>
      </span>
    {{/if}}

    <div class="account-product-item-price">
      {{> core/products/price}}
    </div>

    {{#if show_compare}}
      <label class="form-label account-product-item-compare" for="compare-{{id}}">
        <input type="checkbox" class="form-input form-checkbox" name="products[]" value="{{id}}" id="compare-{{id}}">
        <span class="form-label-text">{{lang 'core.product.compare'}}</span>
      </label>
    {{/if}}
  </div>

  {{#if show_quickshop}}
    <a href="{{url}}" class="account-product-item-quickshop" data-quick-shop data-product-id="{{id}}">{{lang 'core.product.quickshop.view'}}</a>
  {{/if}}
</article>

core/products/price:
{{!
  Shows a product's price.

  Prices are fairly complicated. Besides having to be able to show the price
  with and without tax, a merchant can also assign a Recommended Retail Price,
  and a Sale Price.

  This means that in extreme cases, we have to show 4 different prices at once.

  When a Sale Price is set we do not have direct access to it--instead it
  overwrites the regular price.

  @param price object

  @param show_savings bool
         If true, show a label saying how much is saved from the full price.
}}
{{#if price}}
  <div class="price" itemprop="offers" itemscope itemtype="http://schema.org/Offer">
    {{#if schema_org}}
      <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">

      {{#if price.without_tax}}
        <meta itemprop="price" content="{{price.without_tax.value}}">
      {{else}}
        <meta itemprop="price" content="{{price.with_tax.value}}">
      {{/if}}
    {{/if}}


    {{#if price.without_tax}}
      <span class="price-withouttax" data-price="without-tax">
        {{#if price.rrp_without_tax}}
          <span class="price-rrp" data-price-rrp="without-tax">
            {{price.rrp_without_tax.formatted}}
          </span>
        {{/if}}

        <span class="price-value" data-price-value="without-tax">
          {{price.without_tax.formatted}}
        </span>

        {{#if price.with_tax}}
          <span class="price-tax-label">{{lang 'core.price.without_tax' tax_label=price.tax_label}}</span>
        {{/if}}
      </span>
    {{/if}}


    {{#if price.with_tax}}
      <span class="price-withtax" data-price="with-tax">
        {{#if price.rrp_with_tax}}
          <span class="price-rrp" data-price-rrp="with-tax">
            {{price.rrp_with_tax.formatted}}
          </span>
        {{/if}}

        <span class="price-value" data-price-value="with-tax">
          {{price.with_tax.formatted}}
        </span>

        {{#if price.without_tax}}
          <span class="price-tax-label">{{lang 'core.price.with_tax' tax_label=price.tax_label}}</span>
        {{/if}}
      </span>
    {{/if}}


    {{#if show_savings}}
      {{#if price.saved}}
        <span class="price-saved" data-price-saved>
          {{lang 'core.price.you_save' amount=price.saved.formatted}}
        </span>
      {{/if}}
    {{/if}}
  </div>
{{/if}}

core/products/rating:
{{!
  Shows the product's overall rating as N out of 5 stars.

  @param rating number
         The product's rating.

  @param total number
         The number of votes.
}}
{{#if rating ">=" 0}}
  <div class="rating">
    <div class="rating-stars" aria-hidden="true">
      {{#enumerate 1 5}}
        {{#if ../rating '>=' this}}
          <span class="rating-star rating-star-full"></span>
        {{else}}
          <span class="rating-star rating-star-empty"></span>
        {{/if}}
      {{/enumerate}}
    </div>

    <span class="rating-score">{{rating}}</span>

    {{#if total}}
      <span class="rating-votes">{{lang 'core.rating.total_votes' votes=total}}</span>
    {{/if}}
  </div>
{{/if}}
