components/products/review:
{{#if @index "<" review_count}}
  <article class="review default">
{{/if}}
{{#if @index ">=" review_count}}
  <article class="review supplementary">
{{/if}}
  <header class="review-header">
    <h1 class="review-title">{{title}}</h1>
    <div class="review-rating">
      {{> components/products/ratings rating=rating}}
    </div>
    {{#if name}}
      <span class="review-meta">{{lang 'products.reviews.post_on_by' name=name date=date}}</span>
    {{else}}
      <time class="review-date">{{date}}</time>
    {{/if}}
  </header>
  <section class="review-comment">{{text}}</section>
</article>

core/forms/common/multiline:
{{!
  Textarea form component

  @param validation_message string
         Custom validation message
}}
<div class="form-field form-field-common form-field-multiline {{#if required}}form-required{{/if}}" id="{{id}}">
  <label class="form-label">
    <span class="form-field-title">
      {{label}}
      {{#if required}}<span class="required-text">{{lang 'common.required'}}</span>{{/if}}
    </span>
    <span class="form-field-control">
      <textarea
        class="form-input"
        id="textarea-{{id}}"
        name="{{name}}"
        placeholder="{{prefill}}"
        rows="{{rows}}"
        {{#if required}}
          required
          {{#if validation_message}}
            data-vd-message-required="{{validation_message}}"
          {{/if}}
        {{/if}}
        aria-required="{{required}}">{{value}}</textarea>
    </span>
  </label>
</div>
