components/account-links:
{{#if settings.account_creation_enabled}}
  <span class="account-links">
    {{#if customer}}
      <a href="{{urls.account.index}}">{{lang 'header.logged_in_as' name=customer.shipping_address.first_name}}</a>
      <span class="account-link-separator">&ndash;</span>
      <a href="{{urls.auth.logout}}">{{lang 'header.log_out'}}</a>
    {{else}}
      <a href="{{urls.auth.create_account}}">{{lang 'header.sign_up'}}</a>
      <span class="account-link-separator">&ndash;</span>
      <a href="{{urls.auth.login}}">{{lang 'header.log_in'}}</a>
    {{/if}}
  </span>
{{/if}}

components/currency-selector:
{{#if currency_selector.currencies.length '>' 1}}
  <div class="currency-selector-wrapper">
    <label class="currency-selector-label" for="currency-switcher">
      <span data-currency-selector-toggle>{{currency_selector.active_currency_code}}
        {{> components/common/icons/down-arrow}}
      </span>
    </label>
      <select class="currency-selector">
        {{#each currency_selector.currencies}}
          <option value="{{switch_url}}" {{#if is_active}}selected{{/if}}>
            {{name}}
          </option>
        {{/each}}
      </select>
  </div>
{{/if}}
